import { styled } from 'styled-components';

export const ItemEl = styled.li`
  width: 400px;
  display: flex;
  align-items: flex-start;
  gap: 15px;
  :hover {
    fill: red;
  }
`;

export const ContactData = styled.p`
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 10px;
  gap: 30px;
  border: 1px solid green;
  border-radius: 5px;
  margin-bottom: 5px;
`;
