import React from 'react';
//import PropTypes from 'prop-types'; Проверка типов с помощью PropTypes
import { Formik } from 'formik';
import * as Yup from 'yup';




const formSchema = Yup.object().shape({
    name: Yup.string()
      .matches(/^[a-zA-Z\s]+$/, 'Only letters are allowed')
      .min(3, 'Too Short!')
      .required('This field is required, please fill that'),
    number: Yup.string()
      .matches(/^\d{3}-\d{2}-\d{2}$/, 'Must be in format: 000-00-00')
      .required('This field is required, please fill that'),
  });



const FormContact = ({}) => {
  return (
    <Formik
      initialValues={{
        name: '',
        number: '',
        }}
        validationSchema={formSchema}
      onSubmit={(values, actions) => {
         onSubmit(values);
        actions.resetForm();
      }}
    >
      <Form>

        <div>
        <label htmlFor="name">Name</label>
        <Field type='text'  name="name" placeholder="Name" />
        <ErrorMsg name="name" component="div" />

        <label htmlFor="number">Number</label>
        <Field  type='tel' name="number" placeholder="Number" />

        </div>
        


        <button type="submit">Add contacu</button>
      </Form>
    </Formik>
  );
};
export default FormContact;
