import { Component } from 'react';
import {ItemEl, ContactData, } from './ContactItem.styled'

export class ContactItem extends Component {
  render() {
    const {
      contact: { id, name, number },
      onDeleteContact,
    } = this.props;

    return (
      <ItemEl>
        <ContactData>
          <span>{name}</span>
          <span>{number}</span>
        </ContactData>
        <button 
        type="button" 
        name="delete"
        onClick={() => onDeleteContact(id)}>
          &times;
        </button>
      </ItemEl>
    );
  }
}
